import React, {useContext, us} from 'react'
import {GlobalState} from '../../../GlobalState'

function Cart() {
    const styate = useContext(GlobalState)
    const [cart] = state.userAPI.cart

    if(cart.length === 0)
        return <h2 style={{textAlign: "center", fontSize: "Srem"}}>Cart Empty</h2>

    return (
        <div>
            Cart component
        </div>
    )
}

export default Cart
