import React, { useContext, useState } from 'react'
import {GlobalState} from '../../../GlobalState'
import axios from 'axios'
import {Link} from 'react-router-dom'

function User() {
    const state = useContext(GlobalState)
    const [user, setUser] = useState([])
    const [token] = state.token
    const [Id, SetId] = useState('')
  

    const findUser = async() => {
        
        try {
            const res = await axios.get(`/user/${Id}`, {
                headers: {Authorization: token}
            })
            setUser(res.data)
            console.log(res.data)
        } catch (err) {
            alert(err.response.data.msg)
        }
    }

    return (
        <>
        
        <div class="row mt-5 justify-content-center align-items-center ml-5 mr-5">
            
            <input class="form-control col-md-4" type="text" placeholder="User Id" required
            onChange={e => SetId(e.target.value)} />
            <button class="btn btn-warning col-md-1" onClick={() => findUser()}>Search</button>
            

            <table class="table table-hover container">
                <thead>
                    <tr>
                    <th scope="col">Id</th>
                    <th scope="col">Name</th>
                    <th scope="col">Balance</th>
                    <th scope="col">City</th>
                    <th scope="col">View</th>
                    <th scope="col">Delete</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        user.map(customer => (
                            <tr class="table-primary">
                                <td>{customer.Id}</td>
                                <td>{customer.name.toUpperCase()}</td>
                                <td>{customer.balance}</td>
                                <td>{customer.address3.toUpperCase()}</td>
                                
                                <td><Link type="button" class="btn btn-outline-danger" onClick={() => deleteCustomer(customer.Id)}>Delete</Link></td>
                            </tr>
                        ))
                    }
                
                    
                    
                </tbody>
            </table>
        </div>

        </>
        
    )
}

export default User
