import React, { useContext, useState } from 'react'
import {GlobalState} from '../../../GlobalState'
import axios from 'axios'

function User() {
    const state = useContext(GlobalState)
    const [user, setUser] = useState([])
    const [token] = state.token
    const [Id, SetId] = useState('')

    const findUser = async () => {
        
        try {
            const res = await axios.get(`/user/${Id}`,{
                headers: {Authorization: token}
            })
            console.log(res.data)
        } catch (err) {
            alert(err.response.data.msg)
        }
    }

    return (
        
        <div>
            <form class="form-inline  mt-5 justify-content-center align-items-center">
                <input class="form-control mr-sm-2" type="text" placeholder="User Id" required
                onChange={e => SetId(e.target.value)} />
                <button class="btn btn-warning my-2 my-sm-0" onClick={() => findUser()}>Search</button>
            </form>
        </div>
        
    )
}

export default User
