import React, {useContext, useState, useEffect} from 'react'
import {useParams, Link} from 'react-router-dom'
import {GlobalState} from '../../../GlobalState'
import axios from 'axios'

const initialState = {
    Id: '',
    name: '',
    address1: '',
    address2: '',
    address3: '',
    phone: '',
    balance: 0,
    deposit: 0
}


function Customer() {
    const params = useParams()
    const state = useContext(GlobalState)
    const [customer, setCustomer] = useState(initialState)
    const [token] = state.token
    const [callback, setCallback] = state.customerAPI.callback
    const [onEdit, setOnEdit] = useState(false)
    const [isAdmin] = state.userAPI.isAdmin

    useEffect(() => {
        if(params.Id){
            setOnEdit(true)
            const getCustomer = async () =>{
                const res = await axios.get(`/api/customer/${params.Id}`,{
                    headers: {Authorization: token} 
                })
                setCustomer(res.data)
                console.log(res.data)
            }
            getCustomer()
        }else{
            setOnEdit(false)
            setCustomer(initialState)
        }
    },[callback])

    const handleChangeInputData = e =>{
        const {name, value} = e.target
        setCustomer({...customer, [name]: value})
    }

    const handleChangeInputNumber = e =>{
        const {name, value} = e.target
        setCustomer({...customer, [name]: parseInt(value)})
    }

    const handleChangeInput = e =>{
        const {name, value} = e.target
        setCustomer({...customer, [name]: value.toLowerCase()})
    }

    const handleSubmit = async e =>{
        e.preventDefault()
        try{
            if(!isAdmin) return alert("You're not an admin!")

            if(onEdit){
                await axios.put(`/api/customer/update/${customer.Id}`,{...customer},{
                    headers: {Authorization: token}
                })
            }else{
                await axios.post(`/api/customer/register`,{...customer},{
                    headers: {Authorization: token}
                })
            }
            setCallback(!callback)
        }catch(err){
            alert(err.response.data.msg)
        }
    }

    return (
        <div className="create_product">

        <form onSubmit={handleSubmit}>
            <div className="row">
                <label htmlFor="Id">User ID</label>
                <input type="text" name="Id" id="Id" required
                value={customer.Id} onChange={handleChangeInputData} disabled={onEdit} />
            </div>

            <div className="row">
                <label htmlFor="name">Name</label>
                <input type="text" name="name" id="name" required
                value={customer.name} onChange={handleChangeInput} />
            </div>

            <div className="row">
                <label htmlFor="balance">Balance</label>
                <input type="number" name="balance" id="balance" required
                value={customer.balance} onChange={handleChangeInputNumber} disabled={onEdit} />
            </div>

            <div className="row">
                <label htmlFor="deposit">Deposit</label>
                <input type="number" name="deposit" id="deposit" required
                value={customer.deposit} onChange={handleChangeInputNumber} />
            </div>

            <div className="row">
                <label htmlFor="phone">Deposit</label>
                <input type="text" name="phone" id="phone" required
                value={customer.phone} onChange={handleChangeInputData} />
            </div>

            <div className="row">
                <label htmlFor="address1">No</label>
                <input type="text" name="address1" id="address1" required
                value={customer.address1} onChange={handleChangeInput} />
            </div>

            <div className="row">
                <label htmlFor="address2">Name</label>
                <input type="text" name="address2" id="address2" required
                value={customer.address2} onChange={handleChangeInput} />
            </div>

            <div className="row">
                <label htmlFor="address3">Name</label>
                <input type="text" name="address3" id="address3" required
                value={customer.address3} onChange={handleChangeInput} />
            </div>

            <button type="submit">{onEdit? "Update" : "Create"}</button>
        </form>
    </div>
    )
}

export default Customer
