import React, { useContext, useState } from 'react'
import {GlobalState} from '../../../GlobalState'
import axios from 'axios'

function User() {
    const state = useContext(GlobalState)
    const [user, setUser] = useState([])
    const [token] = state.token
    const [Id, SetId] = useState('')
  

    const findUser = async() => {
        
        try {
            const res = await axios.get(`/user/${Id}`, {
                headers: {Authorization: token}
            })
            setUser(res.data)
            console.log(res.data)
        } catch (err) {
            alert(err.response.data.msg)
        }
    }

    return (
        
        <div class="row mt-5 justify-content-center align-items-center mr-auto ml-auto">
            
            <input class="form-control col-md-4 mr-auto ml-auto" type="text" placeholder="User Id" required
            onChange={e => SetId(e.target.value)} />
            <button class="btn btn-warning col-md-1 mr-auto ml-auto" onClick={() => findUser()}>Search</button>
            
        </div>
        
    )
}

export default User
